mapper.delete:
	update board
    set status = 2
    where no =#{no}

mapper.select:
select board3.no, board3.title, board3.name, board3.register_date, board3.content from (select rownum rn, board2.no, board2.title, board2.name, board2.register_date, board2.content from (select board.no, board.title, member.name, board.register_date, board.content from board join member on (board.member_no = member.no) where board.category = #{category} and board.status != 2
and board.title like '%' || #{searchText} || '%' or board.content like '%' || #{searchText} || '%'
order by board.no desc) board2) board3 where board3.rn between #{startRow} and #{endRow}
